import React, {Component} from 'react';
import Product from './Product/Product';


class Shop extends Component {
    constructor(){
        super();
        this.state = {
            giftCard: {
                id: 1,
                price: 50.00,
                name: '',
                address: '',
                email: ''
            }
        }
    }

    updateState = (e) => {
        e.preventDefault();
        console.log(e)
        this.setState({
            giftCard:{[e.currentTarget.name]: e.currentTarget.value}
        })

    }



    render(){
        console.log(this.state);
        return(
            <div id='shop'>
                <header className="App-header">
                    {/* <img src={logo} className="Shop-logo" alt="logo" /> */}
                    <h1 className="App-header-text">Roxy Shop</h1>
                </header>
                Amount: <input onChange={this.updateState} type="text" name="price"/><br/>
                Name: <input onChange={this.updateState} type="text" name="name"/><br/>
                Address: <input onChange={this.updateState} type="text" name="address"/><br/>
                Email: <input onChange={this.updateState} type="text" name="email"/>
                <Product card={this.state.giftCard}/>
            </div>

        )
    }
}

export default Shop;
