import TextField from '@material-ui/core/TextField';

class Shop extends Component {
    constructor(){
        super();
        this.state = {
            giftCard: {
                id: 1,
                title: 'gift card',
                price: 50.00,
                name: '',
                address: '',
                email: ''
            }
        }
    }

    updatePrice = (e) => {
        e.preventDefault();
        console.log(e)
        this.setState({
            [e.currentTarget.name]: e.currentTarget.value
        })
        console.log(this.state);
    }



    render(){
        const useStyles = makeStyles(theme => ({
            container: {
              display: 'flex',
              flexWrap: 'wrap',
            },
            textField: {
              marginLeft: theme.spacing(1),
              marginRight: theme.spacing(1),
              width: 200,
            },
            dense: {
              marginTop: 19,
            },
            menu: {
              width: 200,
            },
          }));

          const currencies = [
            {
              value: 'USD',
              label: '$',
            },
            {
              value: 'EUR',
              label: '€',
            },
            {
              value: 'BTC',
              label: '฿',
            },
            {
              value: 'JPY',
              label: '¥',
            },
          ];

        return(
            <div id='shop'>
                <header className="App-header">
                    {/* <img src={logo} className="Shop-logo" alt="logo" /> */}
                    <h1 className="App-header-text">Roxy Shop</h1>
                </header>
                <form className={classes.container} noValidate autoComplete="off">
                    <TextField
                        id="standard-name"
                        label="Name"
                        className={classes.textField}
                        value={values.name}
                        onChange={handleChange('name')}
                        margin="normal"
                    />
                </form>
                <Product card={this.state.giftCard}/>
            </div>

        )
    }
}

export default Shop;
