import React, {Component} from 'react';

class SmallPlates extends Component {
    constructor(){
        super();
        this.state = {
            plateItems: [
                {'name': 'Everything But The Kitchen Sink',
                 'description': '24oz made from scratch bloody mary, topped with... lemon, lime, olive, pickle, pepperoncini, onion, celery, cheeseburger slider, buffalo chicken tender, shrimp, thick cut pepper bacon, and yes served with a mini miller high life'}

            ]
        }
    }

    render(){

        const showPlateItems = 
            this.state.plateItems.map((item, index) => {
                return(
                    <div key={index}>
                        <p>{item.name}</p>
                        <span>{item.description}</span>
                    </div>
                )
            })


        return(
            <div id="smallPlates">
                <h3>Brunch</h3>
                <div className="plateList">
                    {showPlateItems}
                </div>
            </div>
        )
    }

}


export default SmallPlates;
