import React, {Component} from 'react';
import Product from './Product/Product';

class Shop extends Component {
    constructor(){
        super();
        this.state = {
            giftCard: {
                id: 1,
                title: 'gift card',
                price: 50.00,
                name: '',
                address: '',
                email: ''
            }
        }
    }

    updatePrice = (e) => {
        e.preventDefault();
        console.log(e)
        this.setState({
            [e.currentTarget.name]: e.currentTarget.value
        })
        console.log(this.state);
    }

    render(){
        return(
            <div id='shop'>
                <header className="App-header">
                    {/* <img src={logo} className="Shop-logo" alt="logo" /> */}
                    <h1 className="App-header-text">Roxy Shop</h1>
                </header>
                <select onChange={this.updatePrice} name="Select Amount" id="price">
                    <option value="10">$10.00</option>
                    <option value="20">$20.00</option>
                    <option value="25">$25.00</option>
                    <option value="50">$50.00</option>
                    <option value="75">$75.00</option>
                    <option value="100">$100.00</option>
                </select>
                <Product card={this.state.giftCard}/>
            </div>

        )
    }
}

export default Shop;
