import React, {Component} from 'react';
import Product from './Product/Product';
import Cart from './Cart/Cart';


class Shop extends Component {
    constructor(){
        super();
        this.state = {
            id: 1,
            price: 50.00,
            name: '',
            address: '',
            email: '',
            itemsInCart: []
        }
    }

    updateState = (e) => {
        e.preventDefault();
        console.log(e)
        this.setState({
            [e.currentTarget.name]: e.currentTarget.value
        })

    }

    handleAddToCartClick = () => {
        console.log("add to cart hit")
        const newID = this.state.id + 1;
        this.setState({
            itemsInCart: [{
                id: this.state.id,
                price: this.state.price.parseInt(),
                name: this.state.name,
                address: this.state.address,
                email: this.state.email
            }],
            price: 50.00,
            name: '',
            address: '',
            email: '',
            id: newID
        })
    };



    render(){
        console.log(this.state);

        const totalCost = this.state.itemsInCart.reduce(
            (acc, item) => acc + item.price * item.quantity,
            0
        );

        return(
            <div id='shop'>
                <header className="App-header">
                    {/* <img src={logo} className="Shop-logo" alt="logo" /> */}
                    <h1 className="App-header-text">Roxy Gift Cards</h1>
                </header>
                <div className="card-details">
                    <h2>Enter Your Information</h2>
                    <div>Amount: <input onChange={this.updateState} type="text" name="price"/></div>
                    <div>Name: <input onChange={this.updateState} type="text" name="name"/></div>
                    <div>Address: <input onChange={this.updateState} type="text" name="address"/></div>
                    <div>Email: <input onChange={this.updateState} type="text" name="email"/></div>
                </div>

                <Product card={this.state} onAddToCartClick={this.handleAddToCartClick}/>
                <Cart itemsInCart={this.state.itemsInCart} totalCost={totalCost}/>
            </div>

        )
    }
}

export default Shop;
